let ionversion = "1.7.4";
let ionunpkg = "https://unpkg.com/ioncss@";
let ionprefix = ionunpkg + ionversion;

function loadStyle(src) {
    return new Promise(function (resolve, reject) {
        let link = document.createElement('link');
        link.href = src;
        link.rel = 'stylesheet';
        link.onload = () => resolve(link);
        link.onerror = () => reject(new Error(`Style load error for ${src}`));
        document.head.append(link);
    });
}

function loadScript(url) {
    var script = document.createElement("script");
    script.src = url; 
    document.head.appendChild(script);  
}

loadStyle(ionprefix + "/style/width.css")

loadStyle(ionprefix + "/style/root.css")
.then(() => loadStyle(ionprefix + "/style/responsive.css"))
.then(() => loadStyle(ionprefix + "/style/color.css"))
.then(() => loadStyle(ionprefix + "/style/components.css"))
.then(() => loadStyle(ionprefix + "/style/flex.css"))
.then(() => loadStyle(ionprefix + "/style/ext.css"))
.then(() => loadStyle(ionprefix + "/style/padding.css"))
.then(() => loadStyle(ionprefix + "/style/margin.css"))
.then(() => loadStyle(ionprefix + "/style/font.css"))
.then(() => loadStyle(ionprefix + "/style/fixed.css"))
.then(() => loadStyle(ionprefix + "/style/align.css"))
.then(() => loadStyle(ionprefix + "/style/border.css"))
.then(() => loadScript(ionprefix + "/main.js"));